+@include("layout.footer")
+
+<script>
+     $('#mytable').DataTable({
+        processing: true,
+        serverSide: true,
+        ajax: "{{route('getdatatableproductsupplier')}}",
+        columns: [
+            { data: 'name', name: 'name' },
+            { data: 'phone', name: 'phone' },
+            { data: 'address', name: 'address' },
+            // { data: 'status', name: 'status' },
+            { data: 'list', name: 'list', searchable:false},
+            { data: 'action', name: 'action' }
+        ]
+    });
+    function success() {
+      $('#mytable').DataTable().ajax.reload(null, false);
+   };
+   function filldatachange(elements){
+       var myid = elements.id;
+       var name = $("#name" + myid).text();
+       var phone = $("#phone" + myid).text();
+       var address = $("#address" + myid).text();
+
+        $("#idchange").val(myid);
+
+       $("#nameedit").val(name);
+       $("#phoneedit").val(phone);
+       $("#addressedit").val(address);
+   }
+   function changesupplier() {
+    //    alert("tes");
+            var myid = $("#idchange").val();
+            var name = $("#nameedit").val();
+            var phone = $("#phoneedit").val();
+            var address = $("#addressedit").val();
+            // alert(name + phone + address);
+            if(name == ""||phone == ""||address == "")
+            {
+                Swal.fire({
+                            type: 'error',
+                            title: 'Empty Field',
+                            text: 'Please Entry the requirement data',
+                            confirmButtonColor: '#e00d0d',
+                        });
+            }
+            else{
+                
+            var myurl =  '{{ url("supplier/") }}' + "/"+ myid;
+            // alert(myurl);
+                $.ajaxSetup({
+                headers: {
+                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
+                }
+                });
+            $.ajax({
+                url: myurl,
+                method: 'PUT',
+                data: {
+                    myname : name,
+                    myphone : phone,
+                    myaddress : address
+                    
+                },
+                success: function (result) {
+                    // alert(result);
+                    if(result == "")
+                    {
+                        success();
+                        Swal.fire({
+                                title: 'Data Changed',
+                                text: 'Data Changed Successfully',
+                                type: 'success',
+                                confirmButtonColor: '#53d408',
+                                allowOutsideClick: false,
+                            }).then((result) => {
+                                $("#myformedit").trigger("reset");
+                                $("#canceledit").click();
+                            });
+                    }
+                    else{
+                        Swal.fire({
+                            type: 'error',
+                            title: 'Data Exists',
+                            text: 'Duplicate Entry For This Product Name',
+                            confirmButtonColor: '#e00d0d',
+                        });
+                    }
+                  
+                  
+                }
+            });
+            }
+          
+        };
+    function addsupplier() {
+            var name = $('#name').val();
+            var phone = $('#phone').val();
+            var address = $('#address').val();
+            if(name == ""||phone == ""||address == "")
+            {
+                Swal.fire({
+                            type: 'error',
+                            title: 'Empty Field',
+                            text: 'Please Entry the requirement data',
+                            confirmButtonColor: '#e00d0d',
+                        });
+            }
+            else{
+                $.ajaxSetup({
+                headers: {
+                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
+                }
+            });
+            $.ajax({
+                url: "{{ route('supplier.store') }}",
+                method: 'POST',
+                data: {
+                    mytipe : "addsupplier",
+                    myname : name,
+                    myphone : phone,
+                    myaddress : address
+                    
+                },
+                success: function (result) {
+                    if(result == "")
+                    {
+                        success();
+                        Swal.fire({
+                                title: 'Data Saved',
+                                text: 'Data Inputted Successfully',
+                                type: 'success',
+                                confirmButtonColor: '#53d408',
+                                allowOutsideClick: false,
+                            }).then((result) => {
+                                $("#myformadd").trigger("reset");
+                                $("#canceladd").click();
+                            });
+                    }
+                    else{
+                        Swal.fire({
+                            type: 'error',
+                            title: 'Data Exists',
+                            text: 'Duplicate Entry For This Supplier Name',
+                            confirmButtonColor: '#e00d0d',
+                        });
+                    }
+                  
+                  
+                }
+            });
+            }
+          
+        };
+</script>
